var mongoose = require('mongoose');
module.exports = function(){
	return mongoose.model('Org',mongoose.Schema({
		name: String,
		id: String,
		domains: [String],
		dictionaries: [mongoose.Schema({
			id: String,
			name: String,
			versions: [mongoose.Schema({
				content: String,
				createdBy: String,
				dateCreated: {
					type: Date,
					default: Date.now(),
					required: true
				}
			})]
		})]
	}));
};
