var findUniqueWords = function(text,minWordLength,next){
	var uniqueWords = {},
		allWords = text
			.replace(/(^\s*)|(\s*$)/gi,'')
			.replace(/[ ]{2,}/gi,' ')
			.replace(/\n /,'\n')
			.split(' ');
	for(var w=0,len=allWords.length;w<len;w++){
		var word = allWords[w];
		if(typeof minWordLength==='number' && word.length<minWordLength)continue;
		if(typeof uniqueWords[word]=='undefined') uniqueWords[word]=1;
		else uniqueWords[word]++;
	}
	if(typeof next==='function')next(null,uniqueWords);
	return uniqueWords;
};

var sortObjectPropertiesByValue = function(obj,doDesc,next){
	var sortable = [];
	for (var prop in obj){
		if(!obj.hasOwnProperty(prop))continue;
		sortable.push({word:prop, count:obj[prop]});
	}
	sortable.sort(function(a, b){
		a.count - b.count;
	});
	if(doDesc)console.log(sortable.reverse());
	if(typeof next==='function')next(null,sortable);
	return sortable;
};